import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { updateTask } from "./features/tasksSlice";  // Action for updating task
import { useNavigate, useParams } from "react-router-dom";  // For navigating back

function EditTaskPage() {
  const { id } = useParams();  // Get task id from URL
  const dispatch = useDispatch();
  const navigate = useNavigate();  // Use useNavigate for navigation
  const task = useSelector((state) =>
    state.tasks.find((task) => task.id === parseInt(id))
  );  // Find the task by ID

  const [title, setTitle] = useState(task?.title || "");
  const [description, setDescription] = useState(task?.description || "");
  const [completed, setCompleted] = useState(task?.completed || false);

  useEffect(() => {
    if (!task) {
      navigate("/");  // If task not found, navigate to home page
    }
  }, [task, navigate]);

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(updateTask({ id: task.id, updates: { title, description, completed } }));
    navigate("/");  // Redirect to home page after saving
  };

  return (
    <div>
      <h1>Edit Task</h1>
      <form onSubmit={handleSubmit}>
        <label>Title</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <label>Description</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <label>
          <input
            type="checkbox"
            checked={completed}
            onChange={() => setCompleted(!completed)}
          />
          Completed
        </label>
        <button type="submit">Save</button>
      </form>
    </div>
  );
}

export default EditTaskPage;
