import React from 'react';
import { Link } from 'react-router-dom';

const TaskItem = ({ task }) => {
  return (
    <li>
      <h3>{task.title}</h3>
      <p>{task.description}</p>
      <p>Status: {task.completed ? 'Completed' : 'Pending'}</p>
      <Link to={`/edit/${task.id}`}>
        <button>Edit</button>
      </Link>
    </li>
  );
};

export default TaskItem;
