import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchTasks } from "./features/tasksSlice";  // Use fetchTasks
import axios from "axios";
import TaskItem from "./TaskItem";
import { useHistory } from "react-router-dom";  // For navigating to the Edit page

function HomePage() {
  const dispatch = useDispatch();
  const tasks = useSelector((state) => state.tasks);
  const history = useHistory();  // Use useHistory for navigation

  useEffect(() => {
    const fetchTasksData = async () => {
      try {
        const response = await axios.get("https://jsonplaceholder.typicode.com/todos");
        dispatch(fetchTasks(response.data));  // Fetch tasks and populate the state
      } catch (error) {
        console.error("Failed to fetch tasks", error);
      }
    };

    fetchTasksData();
  }, [dispatch]);

  // Handle task addition (this function will be used later)
  const handleAddTask = () => {
    history.push("/add-task");  // Navigate to AddTaskPage
  };

  // Handle task edit (this function will be used for the edit button)
  const handleEdit = (id) => {
    history.push(`/edit-task/${id}`);  // Navigate to EditTaskPage
  };

  return (
    <div>
      <h1>To-Do List</h1>
      <button className="add-task-btn" onClick={handleAddTask}>
        Add Task
      </button>
      {tasks.length === 0 ? (
        <p>No tasks available</p>
      ) : (
        <ul>
          {tasks.map((task) => (
            <TaskItem
              key={task.id}
              task={task}
              onEdit={handleEdit}  // Pass the edit function
            />
          ))}
        </ul>
      )}
    </div>
  );
}

export default HomePage;
